import React, { useEffect, useReducer } from 'react'
import PortfolioDataType from '../types/PortfolioDataType'
import { useParams } from 'react-router-dom'
import axios from 'axios'
import { getError } from '../utils'
import { ApiError } from '../types/ApiError'

type State = {
    portfoliodata: PortfolioDataType,
    loading: boolean,
    error: string
}

type Action = 
| {type: 'FETCH_REQUEST'}
| {type: 'FETCH_SUCCESS', payload: PortfolioDataType}
| {type: 'FETCH_FAILED', payload: string}

const initailState:State = {
    portfoliodata: {} as PortfolioDataType,
    loading: true,
    error: ''
}

const reducer = (state:State, action:Action) => {
    switch(action.type) {
        case 'FETCH_REQUEST':
            return {...state, loading: true}
        case 'FETCH_SUCCESS':
            return {...state, loading: false, portfoliodata: action.payload}
        case 'FETCH_FAILED':
            return {...state, loading: false, error: action.payload}
        default:
            return state
    }
}

const ProjectPage = () => {

    const [{loading, error, portfoliodata}, dispatch] = useReducer<React.Reducer<State, Action>>(reducer, initailState)
    const {slug} = useParams()

    useEffect(() => {
        const fetchData = async () => {
            dispatch({type: 'FETCH_REQUEST'})
            try {
                const result = await axios.get(`/api/projects/${slug}`)
                setTimeout(() => {
                    dispatch({type:'FETCH_SUCCESS', payload: result.data})
                })
            } catch (err) {
                dispatch({type:'FETCH_FAILED', payload: getError(err as ApiError)})
            }
        }
        fetchData()
    }, [])

  return (
    <div className="slider-container general px-4 w-[100%]">
        <div className="wrapper">
            <div className="project-page-container">
                <h2 className='project-page-title'>{portfoliodata.name}</h2>
                <div className="project-page-image-container">
                    <img src="" alt="" />
                    <img src="" alt="" />
                    <img src="" alt="" />
                    <img src="" alt="" />
                </div>
                <div className="project-page-content-container">
                    <p className='project-description'></p>
                    <p className='project-frameworks'></p>
                    <p className='project-specials'></p>
                </div>
            </div>
        </div>
    </div>
  )
}

export default ProjectPage
