  //   }, 3000)

  //   return () => {
  //     clearInterval(interval);
  //   };
  // }, [appClass, dispatch, mode])


  return (
    
    <div className="py-4 px-4 w-[100%] general">
      {/* <div className="py-4 px-4 w-[100%] general" style={{boxShadow: `1px 1px 10px ${appClass}` }}></div> */}
      <div className="wrapper w-full flex justify-between gap-4 bg-transparent">
      {/* <div className="wrapper w-full flex justify-between gap-4 bg-transparent" style={{boxShadow: `1px 1px 10px ${appClass}` }}> */}
        <div className="content w-1/2 bg-transparent">
          <div className="content-wrapper relative">
            <div className="text-content-wrapper flex-column">
              <h1 className="font-medium text-5xl mb-4 greeting">
                <span className="first-gretting">H</span>ello People
              </h1>
              <p className="text-3xl mb-4">I am Fronted Developper</p>
              <a target="_blank" href="./files/CV.pdf" className="hero-button spin-icons px-2 py-2 rounded">
                <i className="fa-solid fa-gear"></i><span className="mx-2">Take my CV</span>
                <i className="fa-solid fa-gear"></i>
              </a>
            </div>
          </div>
        </div>

        <div className="hero w-1/2 bg-transparent flex items-center justify-center">
          <div className="hero-photo-wrapper flex justify-center py-8 bg-transparent">
            <img src="./images/my-photo.jpg" alt="photo" />
            
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
