import React, { useEffect, useReducer, useState } from 'react'
import PortfolioDataType from '../types/PortfolioDataType'
import { useParams } from 'react-router-dom'
import axios from 'axios'
import { getError } from '../utils'
import { ApiError } from '../types/ApiError'
import LoadingBox from '../components/LoadingBox'
import MessageErrorBox from '../components/MessageErrorBox'
import './ProjectPage.css'
import ArrowToBottomLeft from "../svg/arrow-to-down-left.svg";
import ArrowToBottomRight from "../svg/arrow-to-down-right.svg";

type State = {
    portfoliodata: PortfolioDataType,
    loading: boolean,
    error: string
}

type Action = 
| {type: 'FETCH_REQUEST'}
| {type: 'FETCH_SUCCESS', payload: PortfolioDataType}
| {type: 'FETCH_FAILED', payload: string}

const initailState:State = {
    portfoliodata: {} as PortfolioDataType,
    loading: true,
    error: ''
}

const reducer = (state:State, action:Action) => {
    switch(action.type) {
        case 'FETCH_REQUEST':
            return {...state, loading: true}
        case 'FETCH_SUCCESS':
            return {...state, loading: false, portfoliodata: action.payload}
        case 'FETCH_FAILED':
            return {...state, loading: false, error: action.payload}
        default:
            return state
    }
}

const ProjectPage = () => {

    const [{loading, error, portfoliodata}, dispatch] = useReducer<React.Reducer<State, Action>>(reducer, initailState)
    const {slug} = useParams()
    const [slideNumber, setSlideNumber] = useState([0, 1, 2, 3])

    useEffect(() => {
        const fetchData = async () => {
            dispatch({type: 'FETCH_REQUEST'})
            try {
                const result = await axios.get(`/api/projects/${slug}`)
                setTimeout(() => {
                    dispatch({type:'FETCH_SUCCESS', payload: result.data})
                })
            } catch (err) {
                dispatch({type:'FETCH_FAILED', payload: getError(err as ApiError)})
            }
        }
        fetchData()
    }, [slug])

  return (
    loading ? <LoadingBox /> : error ? <MessageErrorBox
    >{error}</MessageErrorBox> :
          (
    <div className="slider-container general px-4 w-[100%]">
        <div className="wrapper">
        <h2 className='project-page-title text-center'>{portfoliodata.name}</h2>
            <div className="project-page-container flex gap-8 px-4">
                <div className="project-page-image-container w-[50%]">
                    <div className="div1"><img className='grid-img' src={portfoliodata.images[slideNumber[0]]} alt=''/></div>
                    <div className="div2"><img className='grid-img' src={portfoliodata.images[slideNumber[1]]} alt=''/></div>
                    <div className="div3"><img className='grid-img'  src={portfoliodata.images[slideNumber[2]]} alt=''/></div>
                    <div className="div4"><img className='grid-img' src={portfoliodata.images[slideNumber[3]]} alt=''/></div>
                <div className='arrow-slider arrow-slider-left-bottom'>
                    <img src={ArrowToBottomLeft} alt="ArrowToBottomLeft" />
                </div>   
                <div className='arrow-slider arrow-slider-right-top'>
                    <img src={ArrowToBottomRight} alt="ArrowToBottomRight" />
                </div>  
                               
                </div>
                <div className="project-page-content-container w-[50%]">
                    <p className='project-description'>{portfoliodata.description}</p>
                    <p className='project-frameworks'></p>
                    <p className='project-specials'></p>
                </div>
            </div>
        </div>
    </div>
          ))
}

export default ProjectPage
