import React, { useEffect, useState } from "react";
import "./Pages.css";
import PortfolioData from "../data/PortfolioData";



const Portfolio = () => {

  const [photoNumber, setPhotoNumber] = useState(0)

  function randomInteger(min:number, max:number) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  useEffect(() => {
    const timer = window.setInterval(() => {
      setPhotoNumber(prevphotoNumber => randomInteger(0,4)); // <-- Change this line!
    }, 5000);
    return () => {
      window.clearInterval(timer);
    };
  }, []);

  // setInterval(() => {
  //   setPhotoNumber(randomInteger(0,4))
  //   console.log(photoNumber)
  // }, 5000)

  // useEffect(() => {
    
  // }, [photoNumber])

  return (
    <div className="general px-4 w-[100%]">
      <div className="wrapper">
        <h2 className="text-5xl my-8 text-center greeting font-medium ">
          My Portfolio
        </h2>

        <div className="portfolio-container">
          <div className="portfolio_filter"></div>
          <div className="portfolio-content-container">
            {PortfolioData.map((item) => (
                <img src={item.images[photoNumber]} alt={item.name} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
