import React, { FC } from 'react'
import "./PortfolioCard.css";
import "../SkillsCard/SkillsCard.css";

import PortfolioDataType from '../../types/PortfolioDataType';



type PortfolioCardProps = {
  item: PortfolioDataType,
  imageNumber: number,

};

const PortfolioCard: FC<PortfolioCardProps> = ({item, imageNumber}) => {
  return (
    <div className="card_container my-4" id='big' key={item.name}>
      <div className="card-up-container">
        <h2 className='card-name'>{item.name}</h2>
        <img src={item.images[imageNumber]} alt={item.name} />
      </div>
      <div className="card-down-container">
        <a>{item.github}</a>
        {item.livepage.length > 1 ? (
          <a>{item.livepage}</a>
        ): (
          <p>the database and deployment was deleted due to the termination of free access after graduation</p>
        )}
        <p>{item.description}</p>
        <div className="card-tech grid grid-cols-3">
          {item.tech.map((tech_item) => (
              <p>{tech_item}</p>
          ))}
        </div>
      </div>
    </div>
  )
}

export default PortfolioCard
