import React, { useEffect, useState } from "react";
import "./Pages.css";
import PortfolioData from "../data/PortfolioData";
import PortfolioCard from "../components/Porfolio/PortfolioCard";

const Portfolio: React.FC = () => {
  const [imageNumber, setImageNumber] = useState(0);
  const [openModal, setOpenModal] = useState(true);
  const [filterPortfolio, setFilterPortfolio] = useState("");
  
  const data: string[] = [];

  function randomInteger(min: number, max: number) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  useEffect(() => {
    const timer = window.setInterval(() => {
      setImageNumber((prevImageNumber) => randomInteger(0, 3));
    }, 10000);
    return () => {
      window.clearInterval(timer);
    };
  }, []);

  return (
    <div className="general px-4 w-[100%]">
      <div className="wrapper">
        <h2 className="text-5xl my-8 text-center greeting font-medium ">
          My Portfolio
        </h2>

        <div className="portfolio-container">
          {openModal && (
            <div className="modal-porfolio">
              <p className="modal-porfolio-text">
                Don't forget that this site is also part of my portfolio.
              </p>
              <button
                className="modal-porfolio-button"
                onClick={() => setOpenModal(false)}
              >
                Ok
              </button>
              <span>Learn more</span>
            </div>
          )}

          <div className="portfolio_filter grid grid-cols-4 mx-8 gap-4">
            {PortfolioData.map((Arritem) => (
              <div className="filter-porfolio-item-wrapper">
                {Arritem.tech
                  .filter(function (item, pos) {
                    return Arritem.tech.indexOf(item) === pos;
                  })
                  .map((item) => data.push(item))}
              </div>
            ))}

            {filterPortfolio === "" ? (
              <p
                className="filter-porfolio-item filter-portfolio selected"
                onClick={(event) => {
                  setFilterPortfolio("");
                }}
              >
                All
              </p>
            ) : (
              <p
                className="filter-porfolio-item filter-portfolio"
                onClick={(event) => {
                  setFilterPortfolio("");
                }}
              >
                All
              </p>
            )}

            {data
              .filter(function (item, pos) {
                return data.indexOf(item) === pos;
              })
              .map((item) =>
                filterPortfolio === item ? (
                  <p
                    className="filter-porfolio-item filter-portfolio selected"
                    onClick={(event) => setFilterPortfolio(item)}
                  >
                    {item}
                  </p>
                ) : (
                  <p
                    className="filter-porfolio-item filter-portfolio"
                    onClick={(event) => setFilterPortfolio(item)}
                  >
                    {item}
                  </p>
                )
              )}

            {/* {PortfolioData.map((Arritem) => (
            <div className="filter-porfolio-item-wrapper">{Arritem.tech.filter(function(item, pos){
              return Arritem.tech.indexOf(item)== pos; 
            }).map(item => (
               
              <p className="filter-porfolio-item">{item}</p>
            
                  ))}</div>
                
            ))} */}

            {/* {PortfolioData.map((Arritem) => (
            <div className="filter-porfolio-item-wrapper">{Arritem.tech.filter(function(item, pos){
              return Arritem.tech.indexOf(item)== pos; 
            }).map(item => (
               data.push(item)
              // <p className="filter-porfolio-item">{item}</p>
            
                  )).filter(function(item, pos){
                    return data.indexOf(item:string)== pos; 
                  }).map((item) => (
                    <p className="filter-porfolio-item">{item}</p>
                  ))}</div>
                
            ))} */}

            {/* {PortfolioData.map((Arritem) => (
            <div className="filter-porfolio-item-wrapper">{Arritem.tech.filter(function(item, pos){
              return Arritem.tech.indexOf(item)== pos; 
            }).map(item => (
               
              <p className="filter-porfolio-item">{item}</p>
            
                  ))}</div>
                
            ))} */}

            {/* {PortfolioData.map((Arritem) => (
            <div className="filter-porfolio-item-wrapper">{Arritem.tech.map(item => (
               
              <p className="filter-porfolio-item">{item}</p>
            
                  ))}</div>
                
            ))} */}

            {/* {PortfolioData.map((Arritem) => (
            <>{Arritem.tech.map((item) => (
              <div className="filter-porfolio-item-wrapper"> 
              <p className="filter-porfolio-item">{item}</p>
            </div>
                  ))}</>
                
            ))} */}

            {/* {PortfolioData.map((item1) => (
            <>{item1.tech.map((item2) => (
              
              

              <>{item2}</> */}
            {/* //               <>{item2}.reduce((acc, item) => { */}
            {/* // if (acc.includes(item)) { */}
            {/* //   return acc;
// }
// return [...acc, item]
//               }, [] as Array<{ item: string }>)</>

              // <>{Array.from(new Set(item2 + separator))}</>
            
          //   ))}</>
          // ))} */}

            {/* {PortfolioData.map((Arritem) => (
            {Arritem.tech.map((item) => (
              <div className="filter-porfolio-item-wrapper"> 
              <p className="filter-porfolio-item">{item}</p>
            </div>
                  ))}
                
            ))} */}
          </div>
          <div className="portfolio-content-container grid grid-cols-2">
            {!filterPortfolio
              ? PortfolioData.map((item) => (
                  <PortfolioCard item={item} imageNumber={imageNumber} />
                ))
              : PortfolioData.filter((item) =>
                  item.tech.includes(filterPortfolio)
                ).map((item) => (
                  <PortfolioCard item={item} imageNumber={imageNumber} />
                ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
