import React, { useEffect, useState } from "react";
// import PortfolioDataType from "../types/PortfolioDataType";
import { useParams } from "react-router-dom";
// import axios from "axios";
import { getError } from "../utils";
import { ApiError } from "../types/ApiError";
import LoadingBox from "../components/LoadingBox";
import MessageErrorBox from "../components/MessageErrorBox";
import "./ProjectPage.css";
import ArrowToBottomLeft from "../svg/arrow-to-down-left.svg";
import ArrowToBottomRight from "../svg/arrow-to-down-right.svg";
import { Navbar } from "../components/Navbar/Navbar";
import { useGetProjectDetailsBySlugQuery } from "../hooks/projectHook";
import {
  useCreateCommentMutation,
  useDeleteCommentMutation,
  useGetCommentsQuery,
  useUpdateCommentMutation,
} from "../hooks/commentHook";
import { toast } from "react-toastify";

// type State = {
//   portfoliodata: PortfolioDataType;
//   loading: boolean;
//   error: string;
// };

// type Action =
//   | { type: "FETCH_REQUEST" }
//   | { type: "FETCH_SUCCESS"; payload: PortfolioDataType }
//   | { type: "FETCH_FAILED"; payload: string };

// const initailState: State = {
//   portfoliodata: {} as PortfolioDataType,
//   loading: true,
//   error: "",
// };

// const reducer = (state: State, action: Action) => {
//   switch (action.type) {
//     case "FETCH_REQUEST":
//       return { ...state, loading: true };
//     case "FETCH_SUCCESS":
//       return { ...state, loading: false, portfoliodata: action.payload };
//     case "FETCH_FAILED":
//       return { ...state, loading: false, error: action.payload };
//     default:
//       return state;
//   }
// };

const ProjectPage = () => {
  // const [{ loading, error, portfoliodata }, dispatch] = useReducer<
  //   React.Reducer<State, Action>
  // >(reducer, initailState);
  const { slug } = useParams();
  const [slideNumber, setSlideNumber] = useState([0, 1, 2, 3]);
  const [content, setContent] = useState("");
  const [commentId, setCommentId] = useState("");
  
  const [willEdit, setWillEdit] = useState(false);

  const nextSlide = () => {
    setSlideNumber(
      slideNumber.map((number) => (number < 3 ? number + 1 : number - 3))
    );
  };

  const prevSlide = () => {
    setSlideNumber(
      slideNumber.map((number) => (number > 0 ? number - 1 : number + 3))
    );
  };

  const { data: comments } = useGetCommentsQuery(slug!);
  

  const [dataComments, setDataComments] = useState(comments)
  // console.log(dataComments)

  const { mutateAsync: createComment, isPending } = useCreateCommentMutation(
    slug!
  );

  const { mutateAsync: updateComment } = useUpdateCommentMutation(
    slug!,
    commentId!
  );

  const { mutateAsync: deleteComment } = useDeleteCommentMutation(
    slug!,
    commentId!
  );

  const submitHandler = async (e: React.SyntheticEvent) => {
    e.preventDefault();
    // if (content.length < 5) {
    //   toast.error('Comment must contain at least 5 characters')
    //   return
    // }
    try {
      await createComment({
        content,
      });
      toast.success("Wow!! It was easy...");
      setContent("");
    } catch (err) {
      toast.error(getError(err as ApiError));
    }
  };

  const handleDelete = async (e: React.SyntheticEvent) => {
    e.preventDefault();

    try {
      await deleteComment();
      toast.success("Wow!! It was easy...");
    } catch (err) {
      toast.error(getError(err as ApiError));
    }
  };

  const handleEdit = async (e: React.SyntheticEvent) => {
    e.preventDefault();
    // if (content.length < 5) {
    //   toast.error('Comment must contain at least 5 characters')
    //   return
    // }
    try {
      await updateComment({
        content,
      });
      setContent("");
      // setWillEdit(false)
      toast.success("Wow!! It was easy...");
    } catch (err) {
      toast.error(getError(err as ApiError));
    }
  };

  useEffect(() => {

    setDataComments(comments)
  }, [dataComments, comments])

  // useEffect(() => {
  //   const fetchData = async () => {
  //     dispatch({ type: "FETCH_REQUEST" });
  //     try {
  //       const result = await axios.get(`/api/projects/${slug}`);
  //       setTimeout(() => {
  //         dispatch({ type: "FETCH_SUCCESS", payload: result.data });
  //       });
  //     } catch (err) {
  //       dispatch({ type: "FETCH_FAILED", payload: getError(err as ApiError) });
  //     }
  //   };
  //   fetchData();
  // }, [slug]);

  const {
    data: portfoliodata,
    isLoading,
    error,
  } = useGetProjectDetailsBySlugQuery(slug!);

  return isLoading ? (
    <LoadingBox />
  ) : error ? (
    <MessageErrorBox>{getError(error as unknown as ApiError)}</MessageErrorBox>
  ) : (
    <>
      <Navbar />
      <div className="slider-container general px-4 w-[100%] pt-[110px]">
        <div className="wrapper">
          <div className="project-title-container my-2">
            <h2 className="text-center card-title">{portfoliodata!.name}</h2>
          </div>

          <div className="project-page-container flex gap-8 px-4 my-8">
            <div className="project-page-image-container w-[50%] py-5">
              <div className="div-grid">
                <img
                  className="grid-img"
                  src={portfoliodata!.images[slideNumber[0]]}
                  alt=""
                />
              </div>
              <div className="div-grid">
                <img
                  className="grid-img"
                  src={portfoliodata!.images[slideNumber[1]]}
                  alt=""
                />
              </div>
              <div className="div-grid">
                <img
                  className="grid-img"
                  src={portfoliodata!.images[slideNumber[2]]}
                  alt=""
                />
              </div>
              <div className="div-grid">
                <img
                  className="grid-img"
                  src={portfoliodata!.images[slideNumber[3]]}
                  alt=""
                />
              </div>
              
              
              

              {/* <div
                className="arrow-slider arrow-slider-left-bottom"
                onClick={prevSlide}
              >
                <img src={ArrowToBottomLeft} alt="ArrowToBottomLeft" />
              </div>
              <div
                className="arrow-slider arrow-slider-right-top"
                onClick={nextSlide}
              >
                <img src={ArrowToBottomRight} alt="ArrowToBottomRight" />
              </div> */}
            </div>
            <div className="project-page-content-container w-[50%]">
              <div className="my-8">
                <p>
                  {" "}
                  Github:{"   "}
                  <a
                    target="_blank"
                    rel="noopener noreferrer"
                    className="github-links"
                    href={portfoliodata!.github}
                  >
                    {portfoliodata!.github}
                  </a>
                </p>
                {portfoliodata!.livepage.length > 1 ? (
                  <p>
                    {" "}
                    Livepage:{"   "}
                    <a
                      target="_blank"
                      rel="noopener noreferrer"
                      className="live-links"
                      href={portfoliodata!.livepage}
                    >
                      {portfoliodata!.livepage}
                    </a>
                  </p>
                ) : (
                  <div className="destroyed">
                    <p>
                      <span className="text-white">Livepage:</span>
                      {"   "}livepage was deleted due to the termination of free
                      access after graduation
                    </p>
                  </div>
                )}
              </div>
              <div className="my-8">
                {portfoliodata!.tech.length > 2 ? (
                  <div className="card-tech grid grid-cols-3 gap-3 my-4">
                    {portfoliodata!.tech.map((tech_item) => (
                      <p className="filter-porfolio-item text-center">
                        {tech_item}
                      </p>
                    ))}
                  </div>
                ) : (
                  <div className="card-tech grid grid-cols-2 gap-3 my-4">
                    {portfoliodata!.tech.map((tech_item) => (
                      <p className="filter-porfolio-item text-center">
                        {tech_item}
                      </p>
                    ))}
                  </div>
                )}
              </div>
              <div className="my-8">
                <div className="my-4">{portfoliodata!.description}</div>

                <div className="my-4">
                  <h3 className="features-title">Features:</h3>
                  <ul className="grid grid-cols-2 gap-3 my-4">
                    {portfoliodata!.features.map((item) => (
                      <li className="features-item">
                        <i className="fa-solid fa-star features-icon"></i>
                        {item}
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          </div>
          <div className="flex flex-row-reverse justify-between px-4">
          {willEdit ? (
            
                <form onClick={handleEdit}>
                  <input
                    type="text"
                    id="comment"
                    className="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light"
                    required
                    onChange={(e) => setContent(e.target.value)}
                    value={content}
                  />
                  <button
                    type="submit"
                    className="hero-button spin-icons my-4 px-2 py-2 rounded contact-button front"
                  >
                    <i className="fa-solid fa-gear"></i>
                    <span className="mx-2">Edit Comment</span>
                    <i className="fa-solid fa-gear"></i>
                  </button>
                  <button onClick={() => setWillEdit(false)} className="mx-4" type="button">Cancel</button>
                </form>
              ) : (
                <div className="comment-form">
                  <form onClick={submitHandler}>
                    <input
                      type="text"
                      id="comment"
                      className="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light"
                      required
                      onChange={(e) => setContent(e.target.value)}
                      value={content}
                    />
                    <button
                      type="submit"
                      className="hero-button spin-icons my-4 px-2 py-2 rounded contact-button front"
                      disabled={isPending}
                    >
                      <i className="fa-solid fa-gear"></i>
                      <span className="mx-2">Create Comment</span>
                      <i className="fa-solid fa-gear"></i>
                    </button>
                  </form>
                </div>
              )}
              <div className="comments">
                {comments?.map((comment) => (
                  <div className="flex flex-col">
                    <div key={comment._id} className="comment">
                      <p className="comment_author">{comment.user} said:</p>
                      <p className="comment_body">{comment.content}</p> 
                    </div>
                    <form onClick={handleDelete}>
                      <button
                        type="submit"
                        onClick={() => setCommentId(comment._id)}
                      >
                        Delete Comment
                      </button>
                    </form>
                    <button
                      type="button"
                      onClick={() => {
                        setCommentId(comment._id);
                        setWillEdit(true);
                        setContent("");
                      }}
                    >
                      Edit Comment
                    </button>
                  </div>
                ))}
              </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProjectPage;
