import React, { useEffect, useState } from "react";
import "./Pages.css";
import PortfolioData from "../data/PortfolioData";



const Portfolio = () => {

  const [photoNumber, setPhotoNumber] = useState(0)
  const [openModal, setOpenModal] = useState(true)

  function randomInteger(min:number, max:number) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  useEffect(() => {
    const timer = window.setInterval(() => {
      setPhotoNumber(prevPhotoNumber => randomInteger(0,3)); 
    }, 10000);
    return () => {
      window.clearInterval(timer);
    };
  }, []);


  return (
    <div className="general px-4 w-[100%]">
      <div className="wrapper">
        <h2 className="text-5xl my-8 text-center greeting font-medium ">
          My Portfolio
        </h2>

        <div className="portfolio-container">
          {openModal}
          <div className="modal-porfolio">
            <p className="modal-porfolio-text">Don't forget that this site is also part of my portfolio.</p>
            <button className="modal-porfolio-button">Ok</button>
          </div>
          <div className="portfolio_filter grid grid-cols-4 mx-8">
          {PortfolioData.map((item) => (
                <div className="filter-porfolio-item">

                </div>
            ))}
              
          </div>
          <div className="portfolio-content-container">
            {/* {PortfolioData.map((item) => (
                <img src={item.images[photoNumber]} alt={item.name} />
            ))} */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
