import React, { useState } from 'react'
import "./Slider.css";

const Slider = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const prevSlide = () => {
        setCurrentSlide(currentSlide === 0 ? 2 : (prev) => prev - 1);
      };
      const nextSlide = () => {
        setCurrentSlide(currentSlide === 2 ? 0 : (prev) => prev + 1);
      };

  return (
    <div className='slider'>
        <div className="slider_container" style={{transform:`translateX(-${currentSlide * 100}vw)`}}>
            <div className="slide-one w-full" >1</div>
            <div className="slide-two w-full" >2</div>
            <div className="slide-three w-full" >3</div>
            <div className="slide-three w-full" >3</div>
        </div>
        <div className="icons">
            <div className="icon" onClick={prevSlide}>Arrow Left</div>
            <div className="icon" onClick={nextSlide}>Arrow Right</div>
        </div>
    </div>
  )
}

export default Slider
